package Basic;

public class whileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/**
		 * int i =10; while ( i<=10) {
		 * 
		 * System.out.println(i); i++; }
		 * 
		 * int j=10; while (j>5) {
		 * 
		 * System.out.println(j); j--; }
		 **/

		int k = 30;
		do {
			System.out.println(k);
			k++;

		} while (k < 25);
	}

}
